        "StreamingVideo": function() {
            // FOR SCOPE
            var _this = this;

            if(false == true) {
                var videoVendor = "youtube";
                var mobilePhoto = "/cms/lib/TX02216841/Centricity/Template/GlobalAssets/images///logos/ennis-logo.png";

                if($.trim(mobilePhoto) == "" || mobilePhoto.indexOf("default-man.jpg") > -1) {
                    mobilePhoto = "/cms/lib/NJ50000421/Centricity/Template/17/mobile-video-default.jpg";
                }

                $("#sw-content-container10.region.ui-hp").fullScreenRotator({
                    "videoSource" : videoVendor, // OPTIONS ARE: youtube, vimeo
                    "videoID": "/3Ut_c4Nyx8g", // YouTube and Vimeo ids are set as default in the script
                    "fullScreenBreakpoints" : [960], // OPTIONS ARE [960, 768, 640, 480, 320]
                    "showControls" : true,
                    "showWatchVideoButton" : true,
                    "watchVideoButtonText" : "Watch Background Video",
                    "showProgressBar" : false, // CURRENTLY NOT IN USE
                    "progressBarType" : "line", // CURRENTLY NOT IN USE BUT OPTIONS ARE: line, circle
                    "progressBarCircleWidth": 70, // pixels
                    "progressBarCircleHeight": 70, // pixels
                    "progressBarStrokeWidth": 10, // pixels
                    "progressBarStrokeColor": "#FFF",
                    "showMobileBackgroundPhoto" : true,
                    "mobileBackgroundPhoto" : mobilePhoto,
                    "mobileBackgroundPhotoBreakpoint" : 640, // OPTIONS ARE 768, 640, 480, 320
                    "onReady" : function() { 

                        $(".mute-button, .unmute-button").remove();
                        $(".cs-fullscreen-video-button.watch-video-button").appendTo(".cs-fullscreen-video-buttons").show();
                        $(".cs-fullscreen-video-buttons").appendTo("#cs-fullscreen-video-outer");

                        //VIDEO BUTTON INTERACTION
                        $("#hp-slideshow .cs-fullscreen-video-button.play-button").attr("aria-label", "Play Background Video");
                        $("#hp-slideshow .cs-fullscreen-video-button.pause-button").attr("aria-label", "Pause Background Video");
                        $('#hp-slideshow .cs-fullscreen-video-button.watch-video-button').attr("aria-label", "Watch Background Video").attr('id', 'streaming-video-popout');

                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.play-button", function(e) {
                            if(_this.AllyClick(e)) {
                                $(this).fadeOut();
                                setTimeout(function () {
                                    $("#hp-slideshow .cs-fullscreen-video-button.pause-button").fadeIn().focus();
                                }, 100);
                            }
                        });
                        $("#hp-slideshow").on("click keydown", ".cs-fullscreen-video-button.pause-button", function(e) {
                            if(_this.AllyClick(e)) {
                                $(this).fadeOut();
                                setTimeout(function () {
                                    $("#hp-slideshow .cs-fullscreen-video-button.play-button").fadeIn().focus();
                                }, 100);
                            }
                        });

                        $('#streaming-video-popout').on('click keydown', function(e){
                            if(e.type == "click" || e.keyCode == 13 || e.keyCode == 32){
                                $('body').addClass('video-popup-showing');
                            }
                        });

                        $('body').on('click keydown', '.cs-fullscreen-video-close', function(e){
                            if(e.type == "click" || e.keyCode == 13 || e.keyCode == 32){
	                            $('body').removeClass('video-popup-showing');
							}
                        });
                    },
                    "onStateChange" : function() { }
                });
            }
        },

