        "Search": function() {
            // FOR SCOPE
            var template = this;

            // SUBMIT SEARCH
            $("#gb-search-form").submit(function(e){
                e.preventDefault();
                if($.trim($("#gb-search-input").val()) != "") {
                    window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }

	       });

            //SEARCH INTERACTION
            $("#search-control").on('click keypress', function(event){
                var searchControl = this;

                if(template.AllyClick(event) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();
                    if($(this).parent().hasClass("open")){
                        $(searchControl).parent().removeClass("open");
                        $(searchControl).attr("aria-expanded","false");
                    } else {
                        $(searchControl).parent().addClass("open");
                        $(searchControl).attr("aria-expanded","true");
                        $("#gb-search-input").focus();
                    }
                }
            })
        },

        "AllyClick": function(event) {
            if(event.type === 'click'){
                return true;
            } else if(event.type === 'keypress'){
                var code = event.charCode || event.keyCode;
                if((code === 32)|| (code === 13)){
                    return true;
                }
            } else{
                return false;
            }
        },

        "AllyClickForButtons": function(event) {
            if(event.type === 'click'){
                return true;
            }
            else if(event.type === 'keypress'){
                var code = event.charCode || event.keyCode;
                if(code === 13){
                    return true;
                }
            }
            else{
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

        "AccessibilityFeatures": function(){
            $("#sw-mystart-mypasskey").attr({
                "aria-label":   "Open My Passkey",
                "role":     "button"
            });

            // KEY MAPPING
            var csKeyCodes = { "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40, "tab": 9 };

            //GLOBAL KEYBOARD NAV FOR QUICKLINKS MODAL
            $("#quick-links-icons .cs-global-icon").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();
                            if($(this).parent().is(":first-child")){ //IS LAST ITEM
                                if($(".lt-1024").length){
                                	$("#close-quick-links").focus();
                                } else {
                                	$("#quick-links").focus();
                                }
                            } else {
                                $(this).parent().prev().find(".cs-global-icon").focus();
                            }
                        } else {
                            e.preventDefault();
                            if($(this).parent().is(":last-child")){ //IS LAST ITEM
                            	$("#screen-cover .social-icon:first-child").focus();
                            } else {
                                $(this).parent().next().find(".cs-global-icon").focus();
                            }
                        }
                    break;
                }
            });

            //GLOBAL KEYBOARD NAV FOR QUICKLINKS MODAL
            $("#screen-cover .social-icon").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();
                            if($(this).is(":first-child")){ //IS FIRST ITEM
                            	$("#quick-links-icons .cs-global-icons li:last-child .cs-global-icon").focus();
                            } else {
                                $(this).prev().focus();
                            }
                        } else {
                            e.preventDefault();
                            if($(this).is(":last-child")){ //IS LAST ITEM
                            	if($(".lt-1024").length){
                                	$("#close-quick-links").focus();
                                } else {
                                	$("#quick-links").focus();
                                }

                            } else {
                                $(this).next().focus();
                            }
                        }
                    break;
                }
            });

            $("#close-quick-links").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){

                                e.preventDefault();
                                $("#screen-cover .social-icon:last-child").focus();


                        } else {

                                e.preventDefault();
                                $("#quick-links-icons .cs-global-icons li:first-child .cs-global-icon").focus();

                        }
                    break;
                }
            });

            $("#quick-links").keydown(function(e) {

                // CAPTURE KEY CODE
                switch(e.keyCode) {

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            if($(this).hasClass("open")){
                                e.preventDefault();
                                $("#screen-cover .social-icon:last-child").focus();
                            } 
                        } else {
                            if($(this).hasClass("open")){
                                e.preventDefault();
                                $("#quick-links-icons .cs-global-icons li:first-child .cs-global-icon").focus();
                            }
                        }
                    break;
                }
            });

            //GLOBAL KEYBOARD NAV FOR DD SELECTORS
            $(".ac-dropdown-container .selector").keydown(function(e) {
                var parentContainer = $(e.currentTarget).closest(".ac-dropdown-container").attr("class");
                var parentContainer = parentContainer.split(" ");
                var parentContainer = parentContainer[1];

                // CAPTURE KEY CODE
                switch(e.keyCode) {

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                            $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                        } else {
                            e.preventDefault();

                            $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                            $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                            $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");


                            var loggedIn = false;

                            // CHECK TO SEE IF WE ARE LOGGED IN
                            if($("#sw-mystart-account").length){
                                loggedIn = true
                            }

                            switch(parentContainer){
                                case "user-options":
                                    $(".ac-mystart-button.signout a").focus();
                                break;
                                case "translate":
                                    if(loggedIn == true){
                                        if($("#sw-mystart-mypasskey").length){ 
                                            $("#ui-btn-mypasskey").focus();
                                        } else {
                                            $(".ac-dropdown-container.user-options .selector").focus();
                                        }
                                    } else {
                                        $(".ac-mystart-button.signin a").focus();
                                    }
                                break;
                                case "school-list":
                                    $(".ac-dropdown-container.translate .selector").focus();
                                break;
                            }

                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.down:
                    case csKeyCodes.right:
                        e.preventDefault();

                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();

                    break;
                }
            });

            //GLOBAL KEYBOARD NAV FOR DD LINKS
            $(".ac-dropdown-container li a").keydown(function(e) {
                var parentContainer = $(e.currentTarget).closest(".ac-dropdown-container").attr("class");
                var parentContainer = parentContainer.split(" ");
                var parentContainer = parentContainer[1];
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(".ac-dropdown-container."+parentContainer+" .selector").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case csKeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();

                            // CHECK TO SEE WHAT DROPDOWN WE ARE IN
                            switch(parentContainer){
                                case "translate":
                                    $(".ac-dropdown-container.school-list .selector").focus();
                                    $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                    $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                    $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "user-options":
                                    if($("#sw-mystart-mypasskey").length){ 
                                        $("#ui-btn-mypasskey").focus();
                                    } else {
                                        $(".ac-dropdown-container.translate .selector").focus();
                                    }
                                    $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                    $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                    $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "school-list":
                                    $(".ac-mystart-button.home a").focus();
                                    $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                    $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                    $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;
                            }
                        } else {
                            e.preventDefault();

                            var loggedIn = false;

                            // CHECK TO SEE IF WE ARE LOGGED IN
                            if($("#sw-mystart-account").length){
                                loggedIn = true
                            }
                            // CHECK TO SEE WHAT DROPDOWN WE ARE IN
                            switch(parentContainer){
                                case "translate":
                                    if(loggedIn == true){
                                        if($("#sw-mystart-mypasskey").length){ 
                                            $("#ui-btn-mypasskey").focus();
                                        } else {
                                            $(".ac-dropdown-container.user-options .selector").focus();
                                        }
                                    } else {
                                        $(".ac-mystart-button.signin a").focus();
                                    }
                                    $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                    $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                    $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "user-options":
                                    $(".ac-mystart-button.signout a").focus();
                                    $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                    $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                    $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;

                                case "school-list":
                                    $(".ac-dropdown-container.translate .selector").focus();
                                    $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                    $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                                    $(".ac-dropdown-container."+parentContainer+" .selector").attr("aria-expanded","false");
                                break;
                            }


                        }
                    break;

                    // CONSUME HOME KEY
                    case csKeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case csKeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown-list li:last-child > a").focus();
                    break;

                    case csKeyCodes.esc:
                        $(".ac-dropdown-container."+parentContainer+" .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing");
                        $(".ac-dropdown-container."+parentContainer+"").removeClass("open");
                        $(".ac-dropdown-container."+parentContainer+" .selector").focus();
                    break;
                }
            });
        }
    };

