function searchInput(e, isClick) {
    var inputVal = document.getElementById('search-input').value;
    var isEnterKey = e.keyCode === 13;
    var isInputFocus = $("#search-input:focus");
    
    // add x icon that clears input
    if (data.config.ClearButton && isInputFocus && inputVal.length) {
        $('#clear-icon').show();
        handleClear();
    }
    if ((isInputFocus && isEnterKey) || isClick) {
        var query = inputVal;
        var result = search(query);
        $('#result-items').html(getResultItems(result));
        $('#search-results').fadeIn();
        
        // add click outside listener to close results when user clicks off
        hideOnClickOutside('#search-results');
        var el = document.getElementById("close-search-icon");
        el.removeEventListener("click", closeResults);
        el.addEventListener("click", closeResults);
    }
}

function getResultItems(items) {
    var html = '';
    items = items.slice(0, +data.config.ResultsNum);
    if(items.length) {
        items.forEach(function(item) {
            var itemInfo = item.doc.description || item.doc.h2 || item.doc.h3;
            itemInfo = trimString(itemInfo, data.config.TrimLength);
            html += 
                '
' +
                    ''+item.doc.title.replace('Clear Search Input Close Search Results','')+'' +
                    ''+item.doc.url+'' +
                    '
'+itemInfo+'' +
                '';
        });
    } else {
        html += 
            '
' +
                '
No Results Found...' +
            '';
    }
    return html;
}

function handleSearch() {
    $("#search-input").keyup(function (e) {
        searchInput(e);
    });
}
function handleSearchClick() {
    var el = document.getElementById('search-icon');
    el.onclick = function(e) {
        searchInput(e, true);
    };
}
function handleClear() {
    var el = document.getElementById('clear-icon');
    el.addEventListener("click", clearInput, false);
}

function search(query) {
    var result = Index.search(query, {
        fields: {
            url:            {boost: 2},
            title:          {boost: 7},
            description:    {boost: 4},
            tags:           {boost: 6},
            h1:             {boost: 5},
            h2:             {boost: 3},
            h3:             {boost: 2},
            p:              {boost: 1}
        },
        bool: "OR",
        expand: true
    }); 
    // console.log('Index: ', Index);
    // console.log('result: ', result);

    return result;
}

function initSearch(res) {
    elasticlunr.clearStopWords();
    var customStopWords = data.config.StopWords.split(' ');
    elasticlunr.addStopWords(customStopWords);
    
    Index = elasticlunr(function() {
        this.addField('url');
        this.addField('title');
        this.addField('description');
        this.addField('tags');
        this.addField('h1');
        this.addField('h2');
        this.addField('h3');
        this.addField('p');
        this.setRef('id');
    });

    res.forEach(function(item, i) {
        delete item._type;
        Index.addDoc(item);
    });
}

function initAutocomplete() {
    new Awesomplete('input[data-multiple]', {
    	filter: function(text, input) {
    		return Awesomplete.FILTER_CONTAINS(text, input.match(/[^,]*$/)[0]);
    	},
    	item: function(text, input) {
    		return Awesomplete.ITEM(text, input.match(/[^,]*$/)[0]);
    	},
    	replace: function(text) {
    		var before = this.input.value.match(/^.+,\s*|/)[0];
            this.input.value = before + text;
    	}
    });
}

function fetchData(type, url, data) {
    // console.log('fetching data... from: ', url);
    $.ajax({
        dataType: "json",
        url: encodeURI(url),
        type: type,
        data: data,
        crossDomain: true,
    }).done(function(res) {
        console.log( "Data received: ", res);
        // we were searching for the latest job with the site as tag name
        if(res.jobs) {
            var id = res.jobs[0].id;
            var jobItemsUrl  = 'https://storage.scrapinghub.com/items/'+id+'/?apikey='+API_KEY;
            fetchData('GET', jobItemsUrl);
        }
        // we have our items
        else {
            $.getScript('https://cdnjs.cloudflare.com/ajax/libs/elasticlunr/0.9.6/elasticlunr.min.js', function() {
                initSearch(res);
            });
        }
    }).fail(function(error) {
        console.error( "Critical Error: Not able to load data :", error );
    }).always(function() {
        console.log( "Completed Data Load" );
    });
}

