function videoInfo(vid) {
    var info = '', date = '', name = '';
    if(data.config.Name || data.config.Date) {
        if(data.config.Site == 'yt') {
            data.config.Name? name = ''+vid.snippet.title+'' : '';
            data.config.Date? date = ''+timeSince(vid.snippet.publishedAt)+'' : '';
        } else {
            data.config.Name? name = ''+vid.name+'' : '';
            data.config.Date? date = ''+timeSince(vid.created_time)+'' : '';
        }
        info = '
'+name+date+'';
    }
    return info;
}

function paginateVimeo(page) {
    var vidHeader = {'Authorization': 'Bearer '+VIMEO_ACCESS+''};
    var vidURL = '';
    page? vidURL = 'https://api.vimeo.com'+page : vidURL = 'https://api.vimeo.com/channels/'+data.config.VimChannel+'/videos?fields=pictures.sizes.link,uri,created_time,name,modified_time?page='+page+'&per_page='+relativeVidNum();
    
    var vidData = getData(vidURL, vidHeader);
    
    if(vidData.status != '400') {
        videoGrid(vidData);
    }
    
    return vidData.paging.next;
}


function paginateYouTube(page, uploadsId) {
    var token = '';
    page? token = 'pageToken='+page : '';
    var vidURL = "https://www.googleapis.com/youtube/v3/playlistItems?part=snippet,contentDetails&"+token+"&playlistId="+uploadsId+"&maxResults="+relativeVidNum()+"&key="+YT_KEY;
    var vidData = getData(vidURL);
    
    if(vidData.status != '400') {
        videoGrid(vidData);
    }
    return vidData.nextPageToken;
}

// add image click listener that replaces img with embeded video
function addListener() {
    $('#videos').on('click',".vid-cont", function() {
        // get numerical index from id attr
        var index = $(this).attr('id').replace( /^\D+/g, '');
        // get height of image to set height of video to the same
        var height = $(this).height();
        // replace image with video and set video height to image height
        $(this).replaceWith(EmbedArr[index]);
        $('#vid-'+index).height(height);
    });
}

