// if our desired picture is defined return it, else largest available from end of array
function vimImg(pictures) {
    if(pictures.sizes[3]) return pictures.sizes[3].link;
    else return pictures.sizes[pictures.sizes.length - 1].link;
}

// gets width of video container so we can standardize our video height
function getHeight() {
    var width = $('#videos').width();
    width = width / getDeviceImgNum();
    var height = width * parseFloat(0.5625);
    return height;
}

// gets the current number of images in rows
function getDeviceImgNum() {
    switch(data.device) {
        case 'mobile':
            return correctImgNum(data.config.SmNum);
        case 'tablet':
            return correctImgNum(data.config.MdNum);
        case 'desktop':
            return correctImgNum(data.config.LgNum);
    }
}

// rounds down the number of videos to display per page to always fill rows on each device
function relativeVidNum() {
    var fixed = +data.config.VidNum;
    var mod = fixed % getDeviceImgNum();
    if(mod === 0) return fixed;
    else return fixed - mod;
}

// corrects the data.config.Sm/Md/Lg number of images to display from bootstrap column sizes 
// to actual number of images
function correctImgNum(num) {
    switch (+num) {
        case 12:
            return 1;
        case 6:
            return 2;
        case 4:
            return 3;
        case 3:
            return 4;
        case 2:
            return 6;
    }
}

function getData(url, header) {
    console.log('get url: ', url);
    var data;
    $.ajax({
        headers: header,
        dataType: "json",
        url: url,
        type: 'GET',
        crossDomain: true, 
        async: false
    }).done(function(result) {
        console.log( "Data received: ", result);
        data = result;
    }).fail(function(error) {
        console.error( "Critical Error: Not able to load data", error);
        data = error;
    }).always(function() {
        console.log( "Completed Data Load" );
    });
    
    return data;
}

function timeSince(timeStamp) {
    var now = new Date();
    var date = new Date(timeStamp);
    var secondsPast = (now.getTime() - date.getTime()) / 1000;
    if(secondsPast < 60){
        return parseInt(secondsPast) + 's';
    }
    if(secondsPast < 3600){
        return parseInt(secondsPast/60) + 'm';
    }
    if(secondsPast <= 86400){
        return parseInt(secondsPast/3600) + 'h';
    }
    if(secondsPast > 86400){
        day = date.getDate();
        month = date.toDateString().match(/ [a-zA-Z]*/)[0].replace(" ","");
        year = date.getFullYear() == now.getFullYear() ? "" :  " "+date.getFullYear();
        return day + " " + month + year;
    }
}

