		$ = jQuery;
		function getNewsF ( schoolidi, limiti, displai, aslugi, edkeydc ) { 

			var getUrl = "https://eval.edkey.org/_getnews_api.php?schoolid=" + schoolidi + "&display=" + displai + "&limit=" + limiti + "&aslug=" + aslugi + "&callback=?";
			$.ajax({
			  url: getUrl,
			  async: false,
			  dataType: "jsonp",
			  jsonpCallback: "jsonResourceFeed1",
			  contentType: "application/json",
			  success: function( json ) { 
				$("#newsHolder" + edkeydc).empty();
				if ( json.error !== "" && json.error ) { 
					$("
").html("Sorry, but there were no items in that news feed.").appendTo("#newsHolder" + edkeydc);
				} else { 
					var htmlToWrite = "";
					var newsArticles = json.newsArticles;
					if ( !newsArticles || newsArticles === '' ) { 
						htmlToWrite = "Sorry, there was an error pulling up the news feed.";
					} else { 
						for ( i=0;i<newsArticles.length; i+=1 ) {
							if ( displai === "full" || aslugi != "" ) { // single only
								htmlToWrite+= "
";
								htmlToWrite+= "

";
								htmlToWrite+= "";
								if ( newsArticles[i]['imageCredit'] !== "" ) { htmlToWrite+=""+newsArticles[i]['imageCredit']+""; }
								htmlToWrite+="";
								htmlToWrite+= "
" + newsArticles[i]['ntitle'] + "";
								htmlToWrite+= '' + newsArticles[i]['postedOn'] + '
';
								htmlToWrite+= newsArticles[i]['ncontent'];
								htmlToWrite+= "

";
								htmlToWrite+= newsArticles[i]['imageExtras'];
								htmlToWrite+= "

";
								htmlToWrite+= "";
								//document.title = newsArticles.i.ntitle;
							} else { 
								htmlToWrite+= "
";
								htmlToWrite+= "";
								if ( newsArticles[i]['imageCredit'] !== "" ) { htmlToWrite+=""+newsArticles[i]['imageCredit']+""; }
								htmlToWrite+="";
								htmlToWrite+= "
" + newsArticles[i]['ntitle'] + "";
								htmlToWrite+= newsArticles[i]['postedOn'] + newsArticles[i]['sourceInfo'] + "
";
								htmlToWrite+= newsArticles[i]['nsummary']; 
								htmlToWrite+= "
Read more...
";
								htmlToWrite+= "";
							}
						} 
					}
					document.getElementById('newsHolder').innerHTML = htmlToWrite;
				}
			  }
			}); 
			return true;
		} 
		getNewsF( 13, 3, 'summary', '', '' );


	function newsImageMain ( newsid , imgurl ) {
		if ( $("#showExtraImg" + newsid) ) {
			if ( $("#showExtraImg" + newsid).attr("src") == imgurl ) { $("#showExtraImg" + newsid).toggle(); } else { $("#showExtraImg" + newsid).show(); }
			$("#showExtraImg" + newsid).attr("src", imgurl );
		} 
	}

