			function revslider_showDoubleJqueryError(sliderID) {
				var errorMessage = "Revolution Slider Error: You have some jquery.js library include that comes after the revolution files js include.";
				errorMessage += "
 This includes make eliminates the revolution slider libraries, and make it not work.";
				errorMessage += "

 To fix it you can:
    1. In the Slider Settings -> Troubleshooting set option:  Put JS Includes To Body option to true.";
				errorMessage += "
    2. Find the double jquery.js include and remove it.";
				errorMessage = "" + errorMessage + "";
					jQuery(sliderID).show().html(errorMessage);
			}

(function($) {
    "use strict";

	$(document).ready(function() {
		simple_carousel_cfs();
	});

	$(window).load(function() {
		$('.simple_carousel_init').trigger('destroy');
		simple_carousel_cfs();
	});

	$(window).resize(function() {
		$('.simple_carousel_init').trigger('destroy');
		simple_carousel_cfs();
	});

	function simple_carousel_cfs(){
		$('.simple_carousel_init').carouFredSel({
			scroll   : {
			    items: 1,
				fx : "direct",
		        duration : 1000,                         
		        pauseOnHover : true,
		        easing: 'quadratic',
		    },
		    auto: {
		        play:false,
		        timeoutDuration: 5000
		    },
		    swipe: { 
			    onTouch: true 
			},
		    width: "100%",
		    height: "auto",
		    responsive: true,
		    items: {
		        visible: {
		            min:1,
		            max:2,
		        },
		        height: '100%'
		    },
		    prev: {
		    	button: function() {
	            	return $(this).closest('.simple_carousel_wrapper').find('.simple_carousel_prev');
	            }
	        },
		    next: {
		        button: function() {
		            return $(this).closest('.simple_carousel_wrapper').find('.simple_carousel_next');
		        }
		    }
		});
	};
})(jQuery);

