html.script:
window.jQuery = null;
if (!window.jQuery) {
	var tempScript = document.createElement("script");
	tempScript.src = "http://ajax.googleapis.com/ajax/libs/jquery/1.7.1/jquery.min.js";
	document.getElementsByTagName("head")[0].appendChild(tempScript);
	tempScript.onload = addSmartAppBanner;
} else {
	addSmartAppBanner();
}
function addSmartAppBanner() {

	SmartAppBanner.websiteID = "DESERTWESTSP";
	SmartAppBanner.title = "Educational Networks";
	SmartAppBanner.hideCookieDays = 10;
	SmartAppBanner.description = "Find your school and connect instantly.";
	SmartAppBanner.icon = "/m/pics/edunet_unbranded_logo.jpg";
	SmartAppBanner.button = "OPEN";

	SmartAppBanner.iphoneName = "educational-networks";
	SmartAppBanner.iphoneAppID = "1128350658";


	SmartAppBanner.androidAppID = "mobile.en.com.educationalnetworksmobile.nonBranded";

	SmartAppBanner.defaultIcon = "/m/pics/edunet_unbranded_logo.jpg";
	SmartAppBanner.init();

}
