jQuery(document).ready(function() {
	var extendedDay = jQuery('.wc-pao-addon-preschool-extended-day');
		
	// set defaults		
	extendedDay.removeClass('required-product-addon');
	extendedDay.hide();

	jQuery('#pa_preschool').on('change', function() {
		if (this.value == 'select-any-amount-extended-day')
		{
			extendedDay.addClass('required-product-addon');
			extendedDay.show();
		}
		else
		{
			extendedDay.removeClass('required-product-addon');
			extendedDay.hide();
		}
	});
});

    jQuery(document).ready(function () {
