body.script:
var _paq = _paq || [];
/* tracker methods like "setCustomDimension" should be called before "trackPageView" */
_paq.push(["setDocumentTitle", document.domain + "/" + document.title]);
_paq.push(["setCookieDomain", "*.www.starcityschools.org"]);
_paq.push(['trackPageView']);
_paq.push(['enableLinkTracking']);
(function() {
  var u="//analytics.thrillshare.com/";
  _paq.push(['setTrackerUrl', u+'piwik.php']);
  _paq.push(['setSiteId', '7']);
  var d=document, g=d.createElement('script'), s=d.getElementsByTagName('script')[0];
  g.type='text/javascript'; g.async=true; g.defer=true; g.src=u+'piwik.js'; s.parentNode.insertBefore(g,s);
})();

$(document).ready(function () {
    $('.right-slider').bxSlider({
        mode: 'fade'
        , auto: true
        , autoHover: true
        , adaptiveHeight: true
        , onSliderLoad: function () {
            if ($(window).width() > 1040) {
                var sliderheight = $('#slider').height();
                var headerheight = $('#header').height();
                var windowheight = $(window).height();
                var imgheight = windowheight - headerheight;
                $('.slider-container img, #slider,.slider-container  .bx-wrapper .bx-viewport').css("max-height", imgheight);
                //$('#left-nav .menu-wrap').css("min-height", imgheight);
            }
            else {
                $('.slider-container img, #slider,.slider-container .bx-wrapper .bx-viewport').css("max-height", "100%");
                $('.slider-container img, #slider, .bx-wrapper .bx-viewport').css("min-height", 90);
                //$('#left-nav .menu-wrap').css("height", "auto");
            }
            $(window).resize(function () {
                if ($(window).width() > 1040) {
                    var sliderheight = $('#slider').height();
                    var headerheight = $('#header').height();
                    var windowheight = $(window).height();
                    var imgheight = windowheight - headerheight;
                    $('.slider-container img, #slider, .slider-container  .bx-wrapper .bx-viewport').css("max-height", imgheight);
                    //$('#left-nav .menu-wrap').css("min-height", imgheight);
                }
                else {
                    $('.slider-container img, #slider,.slider-container  .bx-wrapper .bx-viewport').css("max-height", "100%");
                    $('.slider-container img, #slider, .slider-container  .bx-wrapper .bx-viewport').css("min-height", 90);
                    //$('#left-nav .menu-wrap').css("height", "auto");
                }
            });
        }
    });
         $('.news-slider').bxSlider({
      auto: true,
      autoHover: true,
      adaptiveHeight: true,
      onSliderLoad: function (currIndex) {
          var newsheight = $(".news:nth-child(" + (currIndex + 2) + ") .right-content").height()-10;
          $(".main .col-item.news-wrapper .bx-wrapper .bx-pager").css({ bottom: newsheight});
      },
      onSlideBefore: function(currentSlideNumber, totalSlideQty, currentSlideHtmlObject) {
          var newsheight = $(".news .right-content").eq(currentSlideHtmlObject + 1).height()-10;
          $(".main .col-item.news-wrapper .bx-wrapper .bx-pager").css({ bottom: newsheight});
      }
  });
});

$(document).ready(function () {
  document.getElementById("cboxNext").setAttribute("aria-label", "Next Photo");
  document.getElementById("cboxPrevious").setAttribute("aria-label", "Previous Photo");
  document.getElementById("cboxSlideshow").setAttribute("aria-label", "Slide Show");
  var label = document.createElement('label');
  label.setAttribute("for", "filter_id");
  label.setAttribute("style", "display: none;");
  label.innerText = "Change Filter form";
  document.getElementById("filter_id").parentNode.appendChild(label);
  $(".class-link a[href='']").remove();
  $(".folder a[href='#']").remove();
});
