function fetchMoreLatestEvents(modid, direction)
{        
        jQuery.ajax({
                    type : 'POST',
                    dataType : 'json',
                    url : "https://www.auhsd.us/district/index.php?option=com_jevents&ttoption=com_jevents&typeaheadtask=gwejson&file=fetchlatestevents&path=module&folder=mod_jevents_latest&token=1d34d2cd7031633f4421b372276e915e",
                    data : {'json':JSON.stringify({'modid':modid, 'direction':direction})},
                    contentType: "application/x-www-form-urlencoded; charset=utf-8",
                    scriptCharset: "utf-8"
            })                        
                .done(function( data ){                    
                    jQuery("#mod_events_latest_"+modid+"_data").replaceWith(data.html);
                    try {
                        document.getElementById("mod_events_latest_"+modid+"_data").parentNode.scrollIntoView({block: "start", behavior: "smooth"});
                    }
                    catch (e) {
                    }
                })
                .fail(function(x) {
        alert('fail '+x);
                });
}

function vidplay() {
   var video = document.getElementById("bgvid");
   var button = document.getElementById("play");
   if (video.paused) {
      video.play(); 


   } else {
      video.pause();


   }
}

function vidmute() {
   var video = document.getElementById("bgvid");
   var button = document.getElementById("mute");

   if (video.muted) {
      video.muted = false;

   } else {
      video.muted = true; 
   }
}

