		        	$(".global-icon").first().addClass("first");
				$(".global-icon").last().addClass("last");
		        }
		});
		photoGallery();
		pgScreenSize();
		useChannelForSection();
		dynamicContent();	
		if(LTIE9 != 1) {
			drawCurve();
		}

		setMarginTop();

		//$("#hp-app-right").clone().prependTo(".hp.column.one").attr("class","show768");
		$("#baldwin-app").clone().addClass("show480").removeClass("hide480").prependTo("#stripes-container");
		$("#hp-slideshow-inner #hp-app-right").clone().removeClass("hide768").addClass("show768").prependTo(".hp.column.one");
	});	

	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+	

		} if (getWidth >= 768 && getWidth < 1024) { // 768+

		} if (getWidth >= 640 && getWidth < 768) { // 640+

		} if (getWidth >= 480 && getWidth < 640) { // 480+
			appView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			appView();
		}
	}

	function sameHeight() {
		var homeHeight = $("div#hp-content").height();
		if($(".hp.column.two .ui-widget.app").size() <= 1) {
			$("div.hp.column.two .ui-widget-detail").height(homeHeight);
		} else {
			var appHeight = 0;
			$("div.hp.column.two div.ui-widget.app").not($("div.hp.column.two div.ui-widget.app").last()).each(function(){
				appHeight = appHeight + $(this).height();
			});
			lastAppHeight = $("div#hp-content").height() - appHeight + 15;
			$("div.hp.column.two div.ui-widget.app:last .ui-widget-detail").height(lastAppHeight);
		}
		$("div#hp-content .ui-widget-detail div, div#hp-content .ui-widget-detail ul ").resize(function(){
			$("div.hp.column.two .ui-widget-detail").height(auto);
			var homeHeight = $("div#hp-content").height();

			if($(".hp.column.two .ui-widget.app").size() <= 1) {
				$("div.hp.column.two .ui-widget-detail").height(homeHeight);
			} else {
				var appHeight = 0;
				$("div.hp.column.two div.ui-widget.app").not($("div.hp.column.two div.ui-widget.app").last()).each(function(){
					appHeight = appHeight + $(this).height();
				});
				lastAppHeight = $("div#hp-content").height() - appHeight + 15;
				$("div.hp.column.two div.ui-widget.app:last .ui-widget-detail").height(lastAppHeight);
			}
		});
	}

