section.script:
(function(d, s, id) {
						var js, fjs = d.getElementsByTagName(s)[0];
						if (d.getElementById(id)) return;
						js = d.createElement(s); js.id = id;
						js.src = "//connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.7";
						fjs.parentNode.insertBefore(js, fjs);
					}(document, 'script', 'facebook-jssdk'));

body.script:
let linkArr = document.getElementsByTagName('a');
let linkArrLength = linkArr.length;
let exceptions = [
	'cv.k12.ca.us',
	'://cv.',
	'www.cv',
	'tel:(',
	'mailto:',
	'javascript:;',
	'.pdf',
	'sites.google.com/cv.k12.ca.us/'
]

while(linkArrLength--) {
	let external = true
	for(let i = 0; i < exceptions.length; i++){
		if (linkArr[linkArrLength].href.indexOf(exceptions[i]) >= 0){
			external = false;
		}
	}
	if(external){

		linkArr[linkArrLength].onclick = function() {
							alert('IMPORTANT NOTICE: You are leaving this website. Please note that we are not responsible for any content posted on these websites or the design of these websites.');
		}
	}
}

window.onload = init;
var topnavButton = document.getElementById('sitenav_main_mobile_toggle');
var topnavDisplay = document.getElementById('sitenav_main');

function init() {
	topnavButton.onclick = toggleNav;
}

function toggleNav(){
	topnavDisplay.classList.toggle("open");
}
