            	this.StreamingVideo();
            }
            if(this.ShowSchoolList && $(".hp").length) {
                var scrollTop = $(window).scrollTop();
                var schoolsPos = $("#sw-channel-list-outer").outerHeight(true) + $("#hp-slideshow-outer").outerHeight(true);
                schoolsPos = schoolsPos / 2;
                if(scrollTop < schoolsPos) {
                    $(".cs-mystart-dropdown.schools").addClass("open-up");
                } else {
                    $(".cs-mystart-dropdown.schools").removeClass("open-up");
                }
            }
            this.Shortcuts();
            this.ModEvents();
            this.Body();
            this.GlobalIcons();
            this.RsMenu();
            this.AppAccordion();
            this.Footer();
            this.SocialIcons();
            this.ViewFullSite();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });

            csGlobalJs.OpenInNewWindowWarning();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

