                    $(thisHeadlinesArticle).addClass("no-image");
                    $('').prependTo(thisHeadlinesArticle);
                }
                $(thisHeadlinesArticle).wrapInner("
");

                $(thisHeadlinesArticle).find(".ui-article-thumb").prependTo(thisHeadlinesArticle);
            });
			csGlobalJs.OpenInNewWindowWarning();
            $("#sw-mystart-outer").remove();
        },

