        },

        "MMGPlugin": function() {
            // FOR SCOPE
            var _this = this;

            var mmg = eval("multimediaGallery" + $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
            mmg.props.defaultGallery = false;

            var imgWidth, imgHeight;
            if($(window).width() >= 1024 && $(window).width() <= 1320) {
                imgWidth = 658;
                imgHeight = 477;
                $("#hp-slideshow-outer").addClass("small-slideshow");
            } else {
                imgWidth = 750;
                imgHeight = 550;
                $("#hp-slideshow-outer").removeClass("small-slideshow");
            }

            $("#sw-content-container10.ui-hp .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                "efficientLoad" : true,
                "imageWidth" : imgWidth,
                "imageHeight" : imgHeight,
                "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                "galleryOverlay" : false,
                "linkedElement" : [],  // ["image", "title", "overlay"]
                "playPauseControl" : true,
                "backNextControls" : true,
                "bullets" : false,
                "thumbnails" : false,
                "thumbnailViewerNum": [3, 4, 3, 3, 2], // NUMERICAL - [960 view, 768 view, 640 view, 480 view, 320 view]
                "autoRotate" : true,
                "hoverPause" : true,
                "transitionType" : "slide",
                "transitionSpeed" : 2,
                "transitionDelay" : 6,
                "fullScreenRotator" : false,
                "fullScreenBreakpoints" : [960], // NUMERICAL - [960, 768, 640, 480, 320]
                "onImageLoad" : function(props) {
                	//WHOLE IMAGE CLICKABLE
                    if($(".mmg-slide.active .mmg-description-link.read-more", props.element).length && "false" == "true" ) {
                        var getPhotoLink = $(".mmg-slide.active .mmg-description-link.read-more", props.element).attr("href");
                        if(!$(".mmg-slide.active .mmg-image > a", props.element).length) {
                            $(".mmg-slides .mmg-slide.active img", props.element).wrap("");
                        }
                    }
                }, // props.element, props.recordIndex, props.mmgRecords
                "allImagesLoaded" : function(props) {}, // props.element, props.mmgRecords
                "onTransitionStart" : function(props) {}, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                "onTransitionEnd" : function(props) {

                }, // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.mmgRecords
                "allLoaded" : function(props) {
                	//ADJUST CONTROL BUTTON ORDER
                	$(".mmg-control.back").before($(".mmg-control.next"));
                    $(".mmg-control.back").after($(".mmg-control.play-pause"));

                	var windowWidth = $(window).width();
                    if(windowWidth > 1023) {
                      if(!$("#hp-slideshow-outer.small-slideshow").length) {
                          var slideshowWidth = (windowWidth - imgWidth);
                          slideshowWidth = slideshowWidth / 2;
                          slideshowWidth = slideshowWidth + 45;
                          // SET LOGO/SITENAME CONTAINER WIDTH
                          $("#gb-header-outer").css("width",slideshowWidth + "px");

                          // POSITION CONTROL BUTTONS
                          var btnsPos = slideshowWidth + imgWidth;
                          btnsPos = windowWidth - btnsPos;
                          btnsPos = btnsPos - 50;
                          $("#hp-slideshow .mmg-control").css({
                              "left": "calc(100% + " + btnsPos + "px"
                          });
                      }

                      // DESCRIPTION SLIDE
                      $("#hp-slideshow .mmg-slide").each(function() {
                      	var thisSlide = this;

                        if(!$(".mmg-desc-inner",thisSlide).length) {
                            $(".mmg-description",thisSlide).wrapInner("
");
                        }

                        if($(".mmg-description .mmg-desc-inner .mmg-description-title",thisSlide).length) {
                          	$(".mmg-description .mmg-desc-inner",thisSlide).before($(".mmg-description .mmg-desc-inner .mmg-description-title",thisSlide));
                        } else {
                        	$(thisSlide).addClass("no-title");
                        }

                        if($(".mmg-description-link",thisSlide).length) {
                            if(!$(".mmg-description-link span",thisSlide).length) {
                                $(".mmg-description-link",thisSlide).wrapInner("");
                            }
                        }
                      });

                      $("#hp-slideshow .mmg-slide .mmg-description").hover(function() {
                        $(this).addClass("open");
                        $(".mmg-desc-inner", this).slideToggle(300);
                        $("#hp-slideshow .cs-fullscreen-video-overlay").stop(true,true);
                      }, function() {
                      	$(this).removeClass("open");
                        $(".mmg-desc-inner", this).slideToggle(300);
                        $("#hp-slideshow .cs-fullscreen-video-overlay").stop(true,true);
                      });

                      $("#hp-slideshow .mmg-slide.no-title .mmg-description").off();
                    }
                }, // props.element, props.mmgRecords
                "onWindowResize": function(props) {
                	if($(window).width() >= 1024 && $(window).width() <= 1320) {
                    	$("#hp-slideshow-outer").addClass("small-slideshow");
                    } else {
                    	$("#hp-slideshow-outer").removeClass("small-slideshow");
                    }

                	var windowWidth = $(window).width();
                    if(windowWidth > 1023) {
                      if(!$("#hp-slideshow-outer.small-slideshow").length) {
                          var slideshowWidth = (windowWidth - imgWidth);
                          slideshowWidth = slideshowWidth / 2;
                          slideshowWidth = slideshowWidth + 45;
                          // SET LOGO/SITENAME CONTAINER WIDTH
                          $("#gb-header-outer").css("width",slideshowWidth + "px");

                          // POSITION CONTROL BUTTONS
                          var btnsPos = slideshowWidth + imgWidth;
                          btnsPos = windowWidth - btnsPos;
                          btnsPos = btnsPos - 50;
                          $("#hp-slideshow .mmg-control").css({
                              "left": "calc(100% + " + btnsPos + "px"
                          });
                      }
                    }

                    $("#hp-slideshow .mmg-slide").each(function() {
                        var thisSlide = this;

                        if($(".mmg-description-link",thisSlide).length) {
                            if(!$(".mmg-description-link span",thisSlide).length) {
                                $(".mmg-description-link",thisSlide).wrapInner("");
                            }
                        }
                    });
                } // props.element, props.mmgRecords
            });
        },

        "CheckSlideshow": function() {
        	// FOR SCOPE
            var _this = this;

            if($(".hp").length && this.GetBreakPoint() != "desktop" && "Multimedia Gallery" != "Streaming Video") {
                if ($(window).scrollTop() <= $("#hp-slideshow").offset().top + $("#hp-slideshow").height()) {
                    if(this.SlideshowDescFixed) {
                        $("#hp-slideshow").removeAttr("style");

                        this.SlideshowDescFixed = false;
                    }
                } else {
                    if(!this.SlideshowDescFixed) {
                        $("#hp-slideshow").css({
                            "height": $("#hp-slideshow").height(),
                            "overflow": "hidden"
                        });

                        this.SlideshowDescFixed = true;
                    }
                }
            }
        },

