        "RsMenu": function() {
			// FOR SCOPE
			var _this = this;

            // HEADER LINKS
            var headerLinks = [
            	{
                    "text": "Event Calendar",
                    "url": "/Page/2#calendar1",
                    "target": "_self"
                },
                {
                    "text": "Contact Us",
                    "url": "/Page/248",
                    "target": "_self"
                },
                {
                    "text": "Distance Learning Resources",
                    "url": "/domain/221",
                    "target": "_self"
                },
                {
                    "text": "Board of Trustees",
                    "url": "/domain/48",
                    "target": "_self"
                },
                {
                    "text": "PowerSchool Portal",
                    "url": "https://hcsdk8.powerschool.com/public/home.html",
                    "target": "_self"
                },
                {
                    "text": "Hillsborough Recreation",
                    "url": "https://www.hillsboroughrecreation.com/",
                    "target": "_self"
                },
                {
                    "text": "Hillsborough Schools Foundation",
                    "url": "http://www.hsf.org/",
                    "target": "_self"
                },
                {
                    "text": "COVID-19 Information",
                    "url": "/Page/321",
                    "target": "_self"
                }
            ];

            var haveLinks = false;
            var links = [];
            $.each(headerLinks, function(index, link) {
            	if(link.text != "") {
                	links.push({ "text": link.text, "url": link.url, "target": link.target });

                    haveLinks = true;
                }
            });

            var additionalItems = {};
            if(haveLinks) {
            	additionalItems["Popular Links"] = links;
            }

            $.csRsMenu({
                "breakPoint" : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
                "slideDirection" : "left-to-right", // OPTIONS - left-to-right, right-to-left
