$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=TzWwQyK0B6ETx9XuyfgXLA%3d%3d&SessionID=ee8fc138-6499-4495-ada4-a572cfe7fb18&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f24%2f2020+4%3a54%3a29+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.75.14';
