body.script:
if ((/Android|iPhone|iPad|iPod|BlackBerry|Windows Phone/i).test(navigator.userAgent || navigator.vendor || window.opera)) {
	$('body').removeClass('desktop');
}
$(document).ready(function() {

	$(window).on('scroll', function() {
		var delay = 0;
		$('.main .col-item').children().each(function(i) {

			var bottom_of_object = $(this).offset().top + 100;
			var bottom_of_window = $(window).scrollTop() + $(window).height();
			if (bottom_of_window > bottom_of_object) {

				var $li = $(this);
				setTimeout(function() {
					$li.addClass('active');
				}, delay += 100);
			}

		});

	});
	$(window).trigger('scroll');

	$('.slider-container').bxSlider({
		mode: 'fade',
		auto: true,
		autoHover: true,
		adaptiveHeight: true

	});

});

$("button#color-change").click(function(){
                var colorchoice = $('#color-code').val();
    $("#header .search-icon").css('background', colorchoice + " url('assets/apptegy_cms/themes/lemooreunionca/search-icon.png') no-repeat center center");
    $("ul.static-links li span").css('background', colorchoice);

    $(".main .col-item.events-wrapper").css('background', colorchoice);
    $("#footer .footer-col.contact p a").css('color', colorchoice);

});

$(".generated-links").click(function(){
              var colorchoice = $('#color-code').val();
            $(".generated-links li.has-child ul li a").css('border-color', colorchoice);
            $(".generated-links li.has-child ul li ul li a").css('border-color', 'black');
            $(".generated-links li.has-child ul li.has-child ul").css('background', colorchoice);



        });
