var redirectTimer=undefined;
var redirectURL="";
        $(document).on("click", "a", function (e) {
            if (!$(this).hasClass("modal-anchor")) {
                redirectURL = $(this).attr("href");
                if (redirectURL.indexOf("//") >= 0) {

                    if (window.location.host.toLowerCase() != redirectURL.toLowerCase().split("/")[2] && redirectURL.toLowerCase().indexOf("mcoe.org") < 0) {

                        e.preventDefault();

                        $(".modal-anchor").attr("href", redirectURL);
                        $(".modal-anchor").text(redirectURL);
                        $("#redirectModal").modal();

                        var countdown = 8;
                        redirectTimer = setInterval(function () {
                            countdown--;
                            $(".countdown").text(countdown.toString());
                            if (countdown <= 0) {
                                $(".countdown").text("0");
                                window.location = redirectURL;
                            }
                        }, 1000);

                    }
                }
            }
            else {
                redirectURL = "";
            }
    });

	$("#redirectModal").on("hide.bs.modal", function (e) {
        clearInterval(redirectTimer);
        $(".countdown").text("7");
	});
