body.script:
var homeUrl = "https://www.mvla.net/";
function goHome() {
    top.location.href = homeUrl;
}
function languageChange(id) {
    var transUrl = 'http://translate.google.com/translate?u=' + homeUrl + '&sl=en&tl=' + id + '&hl=&ie=UTF-8&anno=0';
    top.location.href = transUrl;
}
