body.script:
        var els = Array.prototype.slice.call(document.querySelectorAll('.dropdown-menu'));

        els.forEach(function (el) {
            el.classList.add('hide-off-screen');
            el.onmouseleave = function () {
                var self = this;
                setTimeout(function () {
                    self.classList.add('hide-off-screen');
                    self.classList.remove('show-on-screen');
                }, 500);
            };
            el.previousElementSibling.onmouseover = function () {
                el.classList.add('show-on-screen');
                el.classList.remove('hide-off-screen');
            };
        });
