function resize_sp1917() {
	var windowWidth = Math.max(parseFloat("1300"), parseFloat(window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth));
	var contentWidth = parseFloat("1200");
	if(contentWidth==0) {
		contentWidth = windowWidth;
	}
	document.getElementById("carousel1917Div").style.width = windowWidth + "px";
	var infoWrap = document.getElementById("carousel1917Div-infowrap");
	if(infoWrap!=null) {
		infoWrap.style.width = windowWidth + "px";
	}
	var transWrap = document.getElementById("carousel1917Div-enImgTransWrap");
	if(transWrap!=null) {
		transWrap.style.width = windowWidth + "px";
	}
	// document.getElementById("carousel1917Div").style.height = Math.max((parseFloat("500")/parseFloat("1300") * windowWidth), parseFloat("500")) + "px";
	document.getElementById("carousel1917Div").style.marginLeft = ((contentWidth - windowWidth) / 2.0) + "px";
	var css = document.createElement("style");
	css.type = "text/css";
	var styles = "#carousel1917Div-infooverlaycontainer div.carouselInfo { margin-left: " + (-1*((contentWidth - windowWidth) / 2.0)) + "px !important; }";
	if (css.styleSheet) css.styleSheet.cssText = styles;
	else css.appendChild(document.createTextNode(styles));
	document.getElementsByTagName("head")[0].appendChild(css);
	document.body.tabIndex = '0';
	document.body.focus();
}

if(window.addEventListener) {
	window.addEventListener("resize", function() { resize_sp1917(); }, false);
} else if(window.attachEvent) {
	window.attachEvent("onresize", function() { resize_sp1917(); });
}
resize_sp1917();

var jS = jQuery.noConflict();

function equalHeights(parent, children) {
	var bxSlider = document.querySelectorAll(parent);
	var maxHeight = document.querySelector(children).clientHeight;
	var sliderPadding = parseInt(jS(parent).css('padding-top')) + parseInt(jS(parent).css('padding-bottom'));
	maxHeight = maxHeight - sliderPadding;

	for (i = 0; i < bxSlider.length; i++) { 
		bxSlider[i].style.minHeight = maxHeight - 5 + "px";
	}
}

