function googleTranslateElementInit() {
    new google.translate.TranslateElement({
        pageLanguage: 'en',
                    layout: google.translate.TranslateElement.InlineLayout.SIMPLE,
                        gaTrack: true,
            gaId: 'UA-109031318-1'
                }, 'google_translate_element');

        }

        Campussuite.Widgets.run({
    "styles": [
        "\/ui\/overrides\/owl-carousel\/owl.carousel.min.css"
    ],
    "scripts": [
        "\/ui\/overrides\/owl-carousel\/owl.carousel.min.js"
    ],
    "plugin": "OwlSlider",
    "pluginId": "cms-widget-2d19525a-2c73-4fc6-95f8-f94c3fef963b",
    "pluginOptions": {
        "transition": "fade",
        "transitionSpeed": 250,
        "navigation": true,
        "pagination": true,
        "paginationNumbers": false,
        "paginationSpeed": 5000,
        "autoplay": true,
        "pauseOnHover": true,
        "autoHeight": false
    }
});

(function (window, document, $, undefined) {
    $(function () {
        $('#cms-widget-980f5b17-553e-4d98-ae18-e25be3269e66').on('click', '.meta-event-attachment-link', function (e) {
            var $target = $(e.currentTarget);
            var p = $.ajax({
                url: $target.attr('data-ajax'),
            });
            p.done(function (resp) {
                $(resp)
                    .appendTo($('body'))
                    .modal({
                        show: false
                    })
                    .on('show.bs.modal', function () {
                        $(this).css('display', 'block');
                        var $d = $(this).find('.modal-dialog'),
                            o = ($(window).height() - $d.height()) / 2,
                            bm = parseInt($d.css('marginBottom'), 10);
                        o = Math.max(o, bm);
                        $d.css('margin-top', o);
                    })
                    .on('hidden.bs.modal', function () {
                        $(this).remove();
                    })
                    .modal('show');
            });
            e.preventDefault();
        });
    });
})(window, document, jQuery);

