$(function () {

    $(window).on('click', closeDeptModal);
    $('#getStaffButton').on('click', getStaffClickHandler);
    $('#getStaffButton').on('click', showModal);

});

function getStaffClickHandler(event) {

    $('#contentDivDeptModal').empty();
    let table = document.createElement('table');
    table.id = 'myDeptTable';
    let span = document.createElement('span');
    span.id = 'closeDeptModal';
    span.className = 'close';
    span.innerHTML = '×';
    $('#contentDivDeptModal').append(span);
    $('#contentDivDeptModal').append(table);

    let groupDN = event.target.dataset.groupdn;

    $('#myDeptTable').DataTable({
        ajax: {
            url: 'https://webappservices.polk-fl.net/c/GenericService.svc/json/SearchStaffDirectoryByMembership?GroupDN=' + groupDN,
            dataSrc: ''
        },
        columns: [
            {
                data : null,
                render : function (data, type, row) {
                    return '' + row.Name + '';
                }
            },
            { data: 'Phone' },
            { data: 'Title' },
            { data: 'Department' },
            { data: 'Office' }
        ]
    });

}

function showModal() {
    $('#deptModal').css('display', 'block');
}

function closeDeptModal(event) {
    if (event.target == document.getElementById('deptModal') || event.target == document.getElementById('closeDeptModal')) {
        $('#deptModal').css('display', 'none');
    }
}



