$(function () {

    // Get the modal
        // Get the modal
    var modal = document.getElementById('myModal');

    // Get the button that opens the modal
    //var btn = document.getElementById("myBtn");

    // Get the  element that closes the modal
    var span = document.getElementsByClassName("close")[0];

    // When the user clicks on the button, open the modal 
    //btn.onclick = function () {
    //    modal.style.display = "block";
    //};

    // When the user clicks on  (x), close the modal
    if (span) {
        span.onclick = function () {
            modal.style.display = "none";
        };
    }


    // When the user clicks anywhere outside of the modal, close it
    window.onclick = function (event) {
        if (event.target === modal) {
            modal.style.display = "none";
        }
    };

	window.onkeypress = function (event) {
        if (event.target === modal) {
            modal.style.display = "none";
        }
    };

    var newurl = 'https://districtwebsiteapp.polk-fl.net/api/events';


    function loadModal(calEvent) {
        var modal = document.getElementById('myModal');
        modal.style.display = "block";

        $('#eventTitleModal').html(calEvent.title);

        var detailTitle;
        var detailDesc;
        var eventDetails = $('#eventDetails');

        $('#eventDetails').empty();

        if (calEvent.startString) {
            var startDate;
            if (calEvent.allDay) {
                startDate = calEvent.startString.slice(0, -8);
            } else {
                startDate = calEvent.startString.replace('12:00 AM', '');
            }
            var allDayEvent = '';
            if (calEvent.allDay && !calEvent.endString) {
                allDayEvent = ' - All Day';
            }

            detailTitle = 'Start Date: ';
            detailDesc = '' + startDate + allDayEvent + '';
            eventDetails.append(detailTitle + detailDesc);
        }

        if (calEvent.endString) {
            var endDate;
            if (calEvent.allDay) {
                endDate = calEvent.endString.slice(0, -8);
            } else {
                endDate = calEvent.endString.replace('12:00 AM', '');
            }
            detailTitle = 'End Date: ';
            detailDesc = '' + endDate + '';
            eventDetails.append(detailTitle + detailDesc);
        }
        //if (calEvent.allDay) {
        //    detailTitle = 'All Day ';
        //    detailDesc = '';
        //    eventDetails.append(detailTitle + detailDesc);
        //}

        if (calEvent.location) {
            detailTitle = 'Location: ';
            detailDesc = '' + calEvent.location + '';
            eventDetails.append(detailTitle + detailDesc);
        }

        if (calEvent.contact) {
            detailTitle = 'Contact: ';
            detailDesc = '' + calEvent.contact + '';
            eventDetails.append(detailTitle + detailDesc);
        }

        if (calEvent.description) {
            detailTitle = 'Description: ';
            detailDesc = '' + calEvent.description + '';
            eventDetails.append(detailTitle + detailDesc);
        }

        if (calEvent.notes) {
            detailTitle = 'Notes: ';
            detailDesc = '' + calEvent.notes + '';
            eventDetails.append(detailTitle + detailDesc);
        }
    }

    $.ajax({
        url: "https://districtwebsiteapp.polk-fl.net/api/events/4",
        success: function (data) {
                       if (data && data.length) {
                for (var i = 0; i < data.length; i++) {
                    var item = data[i];

                    var div = $('
')
                        .addClass('col-md-3');

                    var dateString = item.quickStartString;

                    if (item.quickEndString && item.quickEndString !== item.quickStartString) {
                        dateString = item.quickStartString + ' - ' + item.quickEndString;
                    }

					var location = item.location === null ? '' : item.location

					if(item.cancelled == true) {
						div.append('

' + item.month + '' + item.dayNumber + '

' + item.title + '

' + dateString + '
' + location + '');
					}
					else {
						div.append('

' + item.month + '' + item.dayNumber + '

' + item.title + '

' + dateString + '
' + location + '').addClass('quickEventHome');
					}
					div.data('item', item);

                    $('#quickListHome').append(div);
                }

                $('.quickEventHome').click(function () {
                    event.preventDefault();
                    var data = $(this).data('item');
                    if (data) {
                        console.log(data);
                        loadModal(data);
                    }
                });
            }
        },
        complete: function () {

        }
    });





});

