$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=CKvrxbiplyD6pll8D5MFew%3d%3d&SessionID=78aa0c52-100f-41e1-a6c6-590b30c11f4b&SiteID=4&ChannelID=0&SectionID=0&PageID=1&HitDate=4%2f24%2f2020+8%3a28%3a52+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.10.152';
