	// corrects the positioning of the school list menu for IE browsers.
	function listPos() {
		setTimeout('listLocation()', 200);}
	function listLocation() {
		var listEl = document.getElementById('dm0m1');
		listEl.style.left = "auto";
		listEl.style.right = "0";
		listEl.style.top = "40px";}
	if (!/msie/i.test(navigator.userAgent) && !/opera/i.test(navigator.userAgent)) {
		var schoolMenuIcon = document.getElementById('dm0m0tbl');
		schoolMenuIcon.onmouseover = listPos;}
	// PNG Fix for slide show overlay (IE 6 and earlier)
	var arVersion = navigator.appVersion.split("MSIE")
	var version = parseFloat(arVersion[1])
	if ((version < 7) && (document.body.filters)) 
	{
