link.script:
function faSiteSearch() {
    document.aspnetSearchForm.SearchString.value = $('input[name=SearchString]').val();
    if($('input[name=SearchString]').val() == undefined || $('input[name=SearchString]').val() == "Search"){
        for(i = 0; i < document.aspnetForm.SearchString.length; i++){ 
            if(document.aspnetForm.SearchString[i].value != undefined && document.aspnetForm.SearchString[i].value != "Search"){
                document.aspnetSearchForm.SearchString.value = document.aspnetForm.SearchString[i].value;
                break;
            }
            else
                document.aspnetSearchForm.SearchString.value = document.aspnetForm.SearchString[i].value;
        }
    }
    document.location.href = "/Search?searchPhrase=" + encodeURIComponent(document.aspnetSearchForm.SearchString.value) + "&page=1&perPage=10";
}
function faSiteSearchTextHook(ev) {
    ev = (window.event ? window.event : ev);
    if (ev.keyCode == 10 || ev.keyCode == 13) {
        faSiteSearch();
        stopEventPropagation(ev);
        return false;
    }
    return true;
}

