function reportingIn(moduleName){

  if(!moduleList[moduleName])
    moduleList[moduleName] = {slave: true};

  var thisModule = moduleList[moduleName];

  thisModule.reported = true;

    var found = false;
    var foundReq = "";
    for(var mod in moduleList){
      checkModuleLoaded(mod);
      var eMod = moduleList[mod];
      if (eMod.requires && eMod.requires.length > 0){
        var mods = eMod.requires;
        var modLen = mods.length;
        for(var n=0; n<modLen; n++){
          if (mods[n] == moduleName){
            foundReq = mods[n];
            found = true;
            break;
          }
        }
      }
    }

  Ext.onReady(function(){
    if (found && foundReq && moduleList[foundReq] && moduleList[foundReq].loaded){
      var nodes = Ext.query('.loadingIcon_'+foundReq);
      for (var c=0;c<nodes.length;c++)
        Ext.fly(nodes[c]).removeClass('loadingIcon');
    }
    for(var mod in moduleList){
      if (moduleList[mod].loaded == true){
        var nodes = Ext.query('.loadingIcon_'+mod);
        for (var c=0;c<nodes.length;c++)
          Ext.fly(nodes[c]).removeClass('loadingIcon');
      }
    }
    if (thisModule.loaded){
      var nodes = Ext.query('.loadingIcon_'+moduleName);
      for (var c=0;c<nodes.length;c++)
        Ext.fly(nodes[c]).removeClass('loadingIcon');
    }
  });

}

