function toggleCat(el, cID){
  //var name = el.identify();
  var catItem = Ext.get(el);
  var curVal = true;
  if (catItem.checked != undefined)
    curVal = catItem.checked;

  setTimeout(function(){
    catItem.checked = !curVal;
    catItem.dom.checked = !curVal;
  }, 5);
  if (curVal){
    Ext.select('.cat_'+cID).addClass('hidden');
  }else{
    Ext.select('.cat_'+cID).removeClass('hidden');
  }
}
