Ext.onReady(function(){
  doPrint = function(link){
    printBox = new Ext.Window({
      title: 'Print',
      modal: true,
      resizable: false,
      buttonAlign: 'center',
      layout: 'border',
      height: 170,
      width: 400,
      items: [
                {
                  bodyStyle: 'padding: 10px; text-align: center; font-weight: bold',
                  region: 'north',
                  border: false,
                  height: 30,
                  html: 'What portion of the page would you like to print?'
                },{
                  region: 'center',                    
                  border: false,
                  layout: {
                            type:'vbox',
                            padding:'5',
                            align:'stretch'
                          },
                  items:[{
                              xtype:'button',
                              cls: 'bigPrint1',
                              text: 'Print Entire Page',
                              handler: function(){
                                printBox.close();
                                Ext.Msg.hide();
                                window.print();
                              }
                          },{
                              xtype:'spacer',
                              flex: 1
                          },{
                              xtype:'button',
                              cls: 'bigPrint2',
                              text: 'Print Content Only',
                              handler: function(){
                                printBox.close();
                                Ext.Msg.hide();
                                Ext.Msg.wait('Getting ready to print..','Please Wait...');
                                newLocation = link.href.replace('#','') + '?printview=1';
                                //newLocation = '/print'+lastLoadedURLRel;
                                console.info(newLocation);
                                setTimeout(function(){
                                  var newWindow = window.open(newLocation, '_blank');
                                  newWindow.focus();
                                  Ext.Msg.hide();
                                }, 300);

                              }
                          }]
                }
            ],

      buttons: [
              {
                text: 'Cancel',
                handler: function(){ 
                  printBox.close();
                }
              }
            ]

    });
    printBox.show(this);
  }
});


