  //console.info(columns);
  var portals = [];

  function serializePortals(){
    var columns = [];
    var columnOrders = [];
    var columnOrderQry = [];
    Ext.each(portals,function(portal){
      Ext.each(portal.items.items,function(column,index){
        columns.push(column);
        columnOrders.push([])
      });        
    });
    //alert(columns.toSource());

    Ext.each(columns,function(col, index){
      Ext.each(col.items.items,function(colItem){
        columnOrders[index].push(colItem.storeParams.portletID);
        columnOrderQry.push(index+"_"+colItem.storeParams.portletID);
      });
    });
    //columnOrders.each(function(colOrd, index){
    //  columnOrderQry.push(columnOrders[index].toString());
    //});

    Ext.Msg.wait('Please wait...', 'Saving Portal Position');
    Ext.Ajax.request({
       url: '/ajax/portals/setOrder.cfm',
       method: 'GET',
       params: {id: currentPortalID, s: currentSchool.id, portlets: columnOrderQry.join(',')},
       reader : new Ext.data.JsonReader({
        success : '@success'
       }),
       success: function(response, opts) {
          var obj = Ext.decode(response.responseText);
          if (obj.success){
            Ext.Msg.hide();
          }else{
            Ext.Msg.alert('Error Saving Portal Position', 'This portal could not be moved. You may need to refresh and please try again.');
          }
       },
       failure: function(response, opts) {
        Ext.Msg.alert('Error Saving Portal Position', 'There was a problem communicating with the server. Please check your internet connection and try again.');
       }
    });
  }

  portalCount = 0;
  colCount = 0;    

  portals[portalCount] = new Ext.ux.Portal({
      xtype:'portal_widgets',
      border: false,
      layout: 'column',
      renderTo: 'portalTop',              
      items: makeSingleColumn(columns[colCount], .99, 'margin:0 0 0 10px'),
      listeners: {
        drop: function(e){
          serializePortals();
        }
      }
  });
  portalCount++;
  colCount++;

    colWidth1 = .66;
    colWidth2 = .33;
    midCols = makeTwoColumns(columns[colCount], colWidth1, columns[colCount+1], colWidth2, 'margin:0 0 0 10px');
    colCount += 2;

  portals[portalCount] = new Ext.ux.Portal({
      xtype:'portal_widgets',
      border: false,
      layout: 'column',
      renderTo: 'portalMid',
      items: midCols,
      listeners: {
        drop: function(e){
          serializePortals();
        }
      }
  });
  portalCount++;

}  


var currentPortalID = "1";
var currentPortalLayout = "143";
