$(".group864046").colorbox({rel:'group864046', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group864046").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery864046 = $("a[rel=group864046]").colorbox({maxWidth: "80%"});
$("a#openGallery864046").click(function(e){
    e.preventDefault();
    $gallery864046.eq(0).click();
});

