		var mapOptions = {
			// How zoomed in you want the map to start at (always required)
			zoom: 15,

			// The latitude and longitude to center the map (always required)
			center: new google.maps.LatLng(41.49711265, -88.02393927),



									styles:[]											};


		// Get the HTML DOM element that will contain your map
		// We are using a div with id="map" seen below in the 
		var mapElement = document.getElementById('theme-map-5ea2dd5e0d8e2');

		// Create the Google Map using our element and options defined above
		var map = new google.maps.Map(mapElement, mapOptions);

		// Let's also add a marker while we're at it
		var marker = new google.maps.Marker({
			position: new google.maps.LatLng(41.49711265, -88.02393927),
			map: map,
			title: ''
		});


	});
});

