$(document).ready(function () {
    $(".GoogleSearch").css("display", "block");
    var h = $(".GoogleSearch").outerHeight();
    $(".GoogleSearch").css("margin-top", -h + "px");
    setTimeout(function () {
        $(".GoogleSearch").css("display", "block");
        var h = $(".GoogleSearch").outerHeight();
        $(".GoogleSearch").css("margin-top", -h + "px");
        setTimeout(function () {
            $(".GoogleSearch").css("transition", "all 0.5s");
        }, 20);
    }, 500);
});
function searchClick() {
    $(".GoogleSearch").css("margin-top", "0px");
}
function searchClose() {
    var h = $(".GoogleSearch").outerHeight();
    $(".GoogleSearch").css("margin-top", - h + "px");
}

