    function resizeIFrame(height) {
        var self = $("#IFrame_IRC");
        self.parent(".exhibit-content").height(parseInt(height));
        $(document).trigger("afterContentChange");
    }

    function getIFrameAttributes() {
        var container = $('section.main-content'),
            attributes = { container: container };


        if (container.length > 0) {
            var serviceiframe = document.getElementById('IFrame_IRC');
            $.extend(true, attributes, { iframe: document.getElementById('IFrame_IRC') });

            if (serviceiframe && (null != serviceiframe) && ('undefined' != serviceiframe)) {
                var proxy = $(serviceiframe).attr('proxy')
                                            .replace('%id%', serviceiframe.getAttribute('id'));
                $.extend(true, attributes, { proxy: proxy });
                if (proxy.length > 0) {
                    var target_url_split = proxy.split('/'),
                        target_domain = window.location.protocol + '//' + target_url_split[2];

                    $.extend(true, attributes, { target_domain: target_domain });
                }
            }
        }


        return attributes;
    }

    function pushnotification(command, pd) {    
        pd = pd ? pd : {};
        var attribs = getIFrameAttributes();
        if (attribs && (null != attribs.container)) {
            attribs.iframe.contentWindow.postMessage(JSON.stringify($.extend(true, { notification: command }, pd)), attribs.target_domain);
        }
        return false;
    }

    function modaldisplay(command) {
        var attribs = getIFrameAttributes();
        // console.log(attribs);
        if (attribs && (null != attribs.container)) {
            var div_modal_footer = $('
').addClass('modal-footer')
                                                                   .append($('').addClass('btn')
                                                                                          .attr('data-dismiss', 'modal').attr('aria-hidden', 'true')
                                                                                          .html('Close')),
                        div_modal_load = $('
').attr('class', 'loading-indicator')
                                                         .append($('').attr('src', 'Images/loading.gif')),

                        div_modal_body = $('
').addClass('modal-body').css({ 'max-height': '700px' })
                                                                                        .append(div_modal_load),
                        div_modal_dialog = $('
').addClass('modal-dialog').append($('
').addClass('modal-content')
                                                                                                            .append(div_modal_body)
                                                                                                            .append(div_modal_footer)),
                        div_modal = $('
').attr('id', 'modaldisplay').addClass('modal fade')
                                                    .attr('tabindex', '-1').attr('role', 'dialog')
                                                    .attr('aria-labelledby', 'modaldisplayLabel')
                                                    .attr('aria-hidden', 'true')
                                                    .append(div_modal_dialog)
                                                    .appendTo(attribs.container),
                        div_parentContent = $('
').attr('visibility', 'hidden').attr('class', 'parent-content');

            // Parent window content to be included in the Iframe            
            if (attribs.container.find('[class*="box"]').length > 0) {

                var div_Tab_Explanation = $('
').attr('id', 'ExtendedDefinition')
                                                                      .html($('div[id$="ExtendedDefinition"]').html())
                                                                      .appendTo(div_parentContent),
                    div_Tab_Context = $('
').attr('id', 'Context')
                                                        .html($('div[id$="Context"]').html())
                                                        .appendTo(div_parentContent),
                        div_Tab_Resources = $('
').attr('id', 'SupportingResearch')
                                                            .html($('div[id$="SupportingResearch"]').html())
                                                            .appendTo(div_parentContent);


            }
            if ((null != attribs.iframe)) {
                if ((null != attribs.proxy)) {
                    //plugin support unavailable
                    if ((command.indexOf('pdf') >= 0) && (!jQuery.browser.pdf))
                        attribs.proxy += '&asattachment=true';

                    //if plugin support available
                    var modal_iframe = $('').attr('src', attribs.proxy.replace('%command%', command))
                                                             .attr('frameborder','0')
                                                             .attr('class', 'iframe-default')
                                                             .attr('id', 'contentiframe')
                                                             .attr('visibility', 'hidden')
                                                             .appendTo(div_modal_body);

                    attribs.iframe.contentWindow.postMessage(JSON.stringify({ data: div_parentContent.html().replace(/\n[\s]+/g, ''), title: $('#content-top').html().replace(/\n[\s]+/g, '') }), attribs.target_domain);
                }

                $('#modaldisplay').on('hidden.bs.modal', function () {
                    if (modal_iframe) //ie9 iframe needs to be unloaded before removal
                        modal_iframe.attr('src', 'about:blank');
                    $(this).remove();
                }).on('shown.bs.modal', function () {
                    var _def = new $.Deferred();
                    _def.done(function () {
                        div_modal_load.remove();
                    }).then(function () {
                        if (modal_iframe)
                            modal_iframe.removeAttr('visibility');
                    });
                    setTimeout(function () { _def.resolve(); }, 500);

                }).modal('show');
            }
            else { //When there is no iframe
                window.print();
            }
        } // end of check for container
        return false;
    }

