    $(document).ready(function () {
        $("a[class*=btn]").mouseenter(function () {
            var self = $(this);
            self.tooltip('show');
        });

        $(".content-header").on('click.dropdown', "a[class*='dropdown-item']", function () {
            if (ga) {
                ga('send', 'event', 'Download At-a-Glance PDF', 'click', this.href);
            }
            window.location = 'https://sec.isbe.net/ataglancepdf/PrintToPDF.aspx?rcdts=35050210004&langpref=en' + "&year=" + $(this).attr("data-value");           
        });

        $('.content-header').on('click.card', 'a[data-compare-type]', function (e) {
            switch (e.target.dataset.compareType) {
                case 'base-compare':
                    window.location = "Compare.aspx?sourceid=" + '35050210004';
                    break;
                case 'advanced-compare':
                    window.location = "AdvancedCompare.aspx?sourceid=" + '35050210004';
                    break;
            }

        });

        $('.content-header').on('header.showdetails', function (e, args) {

            args ? $(this).removeClass('collapse') : $(this).addClass('collapse');
            $(this).find('.detail-row').trigger('refresh');
        })
    });

