            function DrawLine() {
                Clear();
                if (text.value != "") {
                    var equation = text.value;
                    ctx.lineWidth = 2;

                    var gradient = ctx.createLinearGradient(0, 0, 500, 0);
                    gradient.addColorStop("0", "red");
                    gradient.addColorStop("0.167", "orange");
                    gradient.addColorStop("0.333", "yellow");
                    gradient.addColorStop("0.5", "green");
                    gradient.addColorStop("0.667", "blue");
                    gradient.addColorStop("0.833", "indigo");
                    gradient.addColorStop("1.0", "purple");

                    ctx.strokeStyle = gradient;
                    ctx.beginPath();
                    var i = 0;
                    var inter = setInterval(function () {
                        i++;
                        var x = (i - 250) / 10;
                        var y = (-eval(equation) + 25) * 10;
                        x = (i - 251) / 10;
                        var yprev = (-eval(equation) + 25) * 10;
                        ctx.moveTo(i - 1, yprev);
                        ctx.lineTo(i, y);
                        ctx.stroke();
                        if (i == 500) {
                            clearInterval(inter);
