div.script:
var modal_image1 = document.getElementById('myModal_image1');
var img_image1 = document.getElementById('image1');
var modalImg_image1 = document.getElementById("myImage_image1");
var captionText_image1 = document.getElementById("caption_image1");
img_image1.onclick = function(){
modal_image1.style.display = "block";
modalImg_image1.src = this.src;
captionText_image1.innerHTML = "";
captionText_image1.innerHTML += '
index.asp';
}
var span_image1 = document.getElementsByClassName("close_image1")[0];
span_image1.onclick = function() {
modal_image1.style.display = "none";
}

