jQuery(document).ready(function() {


    jQuery(".grve-responsive-menu-button").click(function() {
        var switch_val = $("#slide_switch").val();
        if (switch_val == "0") {
            jQuery("#grve-main-menu-responsive").addClass("grve-slide-left");
            jQuery("#grve-theme-wrapper").addClass("grve-slide-left");
            jQuery("#slide_switch").val("1");
        } else {
            jQuery("#grve-main-menu-responsive").removeClass("grve-slide-left");
            jQuery("#grve-theme-wrapper").removeClass("grve-slide-left");
            jQuery("#slide_switch").val("0");
        }
    });

    jQuery(".grve-close-menu-button").click(function() {
        jQuery("#grve-main-menu-responsive").removeClass("grve-slide-left");
        jQuery("#grve-theme-wrapper").removeClass("grve-slide-left");
        jQuery("#slide_switch").val("0");
    });



    jQuery(".grve-menu-scroll > ul > li").mouseenter(function() {
        jQuery(".grve-menu-scroll > ul > li").children("ul").hide();
        jQuery(this).children("ul").slideDown("slow");
    });


    jQuery(".grve-menu-scroll > ul > li").mouseleave(function() {
        jQuery(".grve-menu-scroll ul > li > ul").hide(100);
    });

    jQuery(".grve-menu-scroll > ul > li > ul > li").mouseenter(function() {
        jQuery(".grve-menu-scroll > ul > li > ul > li").children("ul").hide();
        jQuery(this).children("ul").slideDown("slow");
    });


    jQuery(".grve-menu-scroll > ul > li > ul > li").mouseleave(function() {
        jQuery(".grve-menu-scroll ul > li > ul > li > ul").hide(100);
    });

    if((jQuery("#grve-top-bar").length) && (jQuery("#gr_wdt").length)){
       var top_width = jQuery("#grve-top-bar").outerWidth(true)
       if(top_width > 768){
        var top_height  = jQuery("#grve-top-bar").outerHeight(true) + jQuery("#gr_wdt").outerHeight(true);
        jQuery("#grve-anchor-menu").css({"top":top_height+"px", "position":"fixed"});
       }
    }
    if(jQuery("#grve-anchor-menu").length){
      var div_width = jQuery("#grve-anchor-menu").outerWidth(true);
      var menu_width = jQuery("#menu-hr").outerWidth(true); 

      if(div_width > 768){
        jQuery("#menu-hr").css('margin-left',((div_width-menu_width)/2)+'px');
      }
    }

});

function setREVStartSize(e){			
			try {								
				var pw = document.getElementById(e.c).parentNode.offsetWidth,
					newh;
				pw = pw===0 || isNaN(pw) ? window.innerWidth : pw;
				e.tabw = e.tabw===undefined ? 0 : parseInt(e.tabw);
				e.thumbw = e.thumbw===undefined ? 0 : parseInt(e.thumbw);
				e.tabh = e.tabh===undefined ? 0 : parseInt(e.tabh);
				e.thumbh = e.thumbh===undefined ? 0 : parseInt(e.thumbh);
				e.tabhide = e.tabhide===undefined ? 0 : parseInt(e.tabhide);
				e.thumbhide = e.thumbhide===undefined ? 0 : parseInt(e.thumbhide);
				e.mh = e.mh===undefined || e.mh=="" || e.mh==="auto" ? 0 : parseInt(e.mh,0);		
				if(e.layout==="fullscreen" || e.l==="fullscreen") 						
					newh = Math.max(e.mh,window.innerHeight);				
				else{					
					e.gw = Array.isArray(e.gw) ? e.gw : [e.gw];
					for (var i in e.rl) if (e.gw[i]===undefined || e.gw[i]===0) e.gw[i] = e.gw[i-1];					
					e.gh = e.el===undefined || e.el==="" || (Array.isArray(e.el) && e.el.length==0)? e.gh : e.el;
					e.gh = Array.isArray(e.gh) ? e.gh : [e.gh];
					for (var i in e.rl) if (e.gh[i]===undefined || e.gh[i]===0) e.gh[i] = e.gh[i-1];
										
					var nl = new Array(e.rl.length),
						ix = 0,						
						sl;					
					e.tabw = e.tabhide>=pw ? 0 : e.tabw;
					e.thumbw = e.thumbhide>=pw ? 0 : e.thumbw;
					e.tabh = e.tabhide>=pw ? 0 : e.tabh;
					e.thumbh = e.thumbhide>=pw ? 0 : e.thumbh;					
					for (var i in e.rl) nl[i] = e.rl[i]nl[i] && nl[i]>0) { sl = nl[i]; ix=i;}															
					var m = pw>(e.gw[ix]+e.tabw+e.thumbw) ? 1 : (pw-(e.tabw+e.thumbw)) / (e.gw[ix]);					

					newh =  (e.type==="carousel" && e.justify==="true" ? e.gh[ix] : (e.gh[ix] * m)) + (e.tabh + e.thumbh);
				}			
				
				if(window.rs_init_css===undefined) window.rs_init_css = document.head.appendChild(document.createElement("style"));					
				document.getElementById(e.c).height = newh;
				window.rs_init_css.innerHTML += "#"+e.c+"_wrapper { height: "+newh+"px }";				
			} catch(e){
				console.log("Failure at Presize of Slider:" + e)
			}					   
		  };

