	/*  */
	$("#CT_Header_0_ctl00_drpQuickLinks").change(function() {
		var selectedItem = this.options[this.selectedIndex];
		var selectedValue = selectedItem.value;
		var QuickLinks_array = selectedValue.split(";");

		if (QuickLinks_array.length > 1 && QuickLinks_array[0]) {
			var url
			if (QuickLinks_array[0].substring(0, 4) == 'http') {
				url = QuickLinks_array[0];
			}
			else {
				url = 'http://' + 'www.olchs.org' + QuickLinks_array[0];
			}
			// check if externalLinks module is used on this site..             
			if (window.safeList) {
				var caption = $(selectedItem).text();
				var urlhost = url.replace(/^(([^\/]+?\.)*)([^\.]{4,})((\.[a-z]{1,4})*)$/, '$3$4');
				urlhost = getHost(urlhost);
				
				var host = window.location.host.replace(/^(([^\/]+?\.)*)([^\.]{4,})((\.[a-z]{1,4})*)$/, '$3$4');                
				
				if (jQuery.inArray(urlhost, window.safeList) < 0 && url.match(/^http/) && !url.match(host)) {
					if (QuickLinks_array[1] == '1')
						ShowExternalLinkMessage(caption, url, false, "_blank");
					else
						ShowExternalLinkMessage(caption, url, false);
				}
				else {
					if (QuickLinks_array[1] == '1') open(url);
					else location.href = url;
				}
			}
			else {
				if (QuickLinks_array[1] == '1'){

                    var windowSize = "width=" + window.innerWidth + ",height=" + window.innerHeight + ",scrollbars=yes";
                    
                    if( /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent) ) {
                        location.href = url;
                        /* some mobile devices are not working with window.open */
                    }
                    else {
                        window.open(url);
                    }

                    
                }
				else
					location.href = url;
			}
		}
		return false;
	});
