;(function($) {
  var href = window.location.href;
  if (href.indexOf('?') === -1) {
    href += '?template=m';
  } else {
    href += '&template=m';
  }
  $('#copyRight .mobileSite').attr('href', href).click(function() {
    // Reset the session cookie to redirect to mobile.
    $.cookie('socs_MobileSwitch', 'y', { path: '/' });
  });
})(jQuery);
