    function passThrough(e) {
        $("a.tranLink img").each(function () {
            // check if clicked point (taken from event) is inside element
            debugger;
            var mouseX = e.pageX;
            var mouseY = e.pageY;
            var offset = $(this).offset();
            var width = $(this).width();
            var height = $(this).height();

            if (mouseX > offset.left && mouseX < offset.left + width
                && mouseY > offset.top && mouseY < offset.top + height) {

                $(this).click(); // force click event
            }
        });
    }

    $(".header-jpeg").click(passThrough);
    $(".header-png").click(passThrough);



    // JavaScript and JQUERY for header navigation with bubbles and arrows
    //controls when the animation gets turned back on
    var rSlider = null;

    //For "HEADERBUBBLESWITHARROWS".  When an arrow is clicked, icrements the count and shows that image. 
    function arrowClick(x) {

        try {
            //counts the position of the arrows
            var clicks = 0;

            //reset all bubble colors
            resetBubbleColors();

            //get the count of all the images we have
            var ct = parseInt(document.getElementById('bubblecount').value) - 1;

            //get the current bubble
            var currBubble = document.getElementById('currentbubble');

            //if we are moving forward then increment the count by one
            if (x == "next") {
                clicks = parseInt(currBubble.value) + 1;

                //reset to 0 if we are at the end
                if (clicks > ct) {
                    clicks = 0;
                }
            }
            else {
                clicks = parseInt(currBubble.value) - 1;
                //reset to the end if we are at 0
                if (clicks < 0) {
                    clicks = ct;
                }
            }

            //Display the image
            showBubbleImage(clicks, document.getElementById("headerimage" + clicks).value, "arrow");

            //remember where we are
            currBubble.value = clicks;
        }
        catch (err) {
        }
    }


    //Display the image assigned to the bubble
    function showBubbleImage(x, y, z) {
        try {

            //reset buubble colors
            resetBubbleColors();

            //hide the image gallery and turn the photo displayer (showimage) on
            document.getElementById('showimage').style.visibility = "visible";
            document.getElementById('gallery').style.visibility = "hidden";

            //Set "slideron" to "on".  This is referenced in /js/jquery.cycle.all.min to tell the cycler when (and when not)
            //to alter bubble colors.  If "off" then we do not want cycle altering our bubbles since that will be controlled 
            //here by a user clicking on a bubble.
            document.getElementById('slideron').value = "Off";

            //set the image for showimage
            var lnk = document.getElementById('headerimageurl' + x).value
            if (lnk != "") {
                lnk = "";
            }


            //set the image for showimage
            document.getElementById('showimage').innerHTML = lnk + "

" + document.getElementById('headerimagename' + x).value + "" + document.getElementById('headerimagedesc' + x).value + "";

            //reset the slider
            clearSliderInterval();

            //now set the current bubble to black (meaning it is the current pucture being viewed).
            $(document.getElementById('bubble' + x)).animate({ backgroundColor: 'black' }, 'slow');

            document.getElementById('currentbubble').value = x;

            return false;
        }
        catch (err) {
        }
    }


    //After a 5 second pause from the last bubble click, we want to show the slider again
    function ResetSlider() {
        try {

            //turn the slider on
            document.getElementById('slideron').value = "On";

            //turn the image view off
            document.getElementById('showimage').style.visibility = "hidden";

            //only run if thegallery is set to hidden
            if (document.getElementById('gallery').style.visibility == "hidden") {
                //re-syart the header cycle to run on its own again
                $(document).ready(function () { $('.transitions_skin').cycle({ fx: 'fade', speed: 4000, timeout: 500, prev: '#prev', next: '#next', pager: '#nav', pause: 1 }).removeClass('progress').fadeIn(); });

                //set bubble #1 to highlight
                $(document.getElementById('bubble0')).animate({ backgroundColor: 'black' }, 'slow');
            }
            //put here just to ensure the gallery is turned back on
            document.getElementById('gallery').style.visibility = "visible";
            document.getElementById('currentbubble').value = "-1";
        }
        catch (err) {
        }
    }


    //if rSlider has a value then clear (cancel) the previous timeout.  We want to do this each click so the timer only
    //runs after a pause of 5 seconds from the last click.
    function clearSliderInterval() {
        try {
            //if the slider iss set then cancel the last interval
            if (rSlider) {
                clearInterval(rSlider);
                rSlider = null;
            }
            //now set the slider timeout to 5 seconds.  The slider will appear again after a 5 second delay from the last click.
            rSlider = setInterval(ResetSlider, 8000);
        }
        catch (err) {
        }
    }


    //reset all bubble colors to stop the animation and clear their color
    function resetBubbleColors() {
        try {
            //reset all bubble colors from any previous clicks
            var ct = document.getElementById('bubblecount').value;
            for (i = 0; i < ct; i++) {
                $(document.getElementById('bubble' + i)).stop(true);
                document.getElementById('bubble' + i).style.backgroundColor = "white";
            }
        }
        catch (err) {
        }
    }


    //display the Left/Right arrows
    function showArrowsOn() {
        try {
            //turn the arrows on
            document.getElementById('arrowleft').style.visibility = "visible";
            document.getElementById('arrowright').style.visibility = "visible";

            //reset the slider so when we mose out it will restart
            clearSliderInterval();
        }
        catch (err) {
        }
    }

