link.script:
function quickLinksMover() {
    if (window.innerWidth < 991) {
        $(".UpperHeader li").clone().appendTo(".slicknav_nav");
        $(".search").appendTo(".slicknav_nav");
    }
    else {
        $(".search").appendTo(".Upperheader .container-fludi");
    }
    }
    $(document).ready(function () {
        quickLinksMover();
    });

