main.script:
        /*  */
        $(document).ready(function() {
            var val = $get('CT_Slideshow_1_hdnTime').value;
            if (val == '') val = 50;
            val *= 1000;

			var owl = $(".owlImageBox .owl-carousel"),
				slideTextList = $(".mobileTxt");

			owl.owlCarousel({
			    items : 1,
                autoPlay: val,
				transitionStyle : "fade",
				slideSpeed : 300,
				paginationSpeed : 400,
				singleItem: true,
				//loop: true,
				afterMove: function(carousel) {
					slideTextList.find(".active").removeClass("active");
					slideTextList.find(".owlTxt:eq("+ this.currentItem +")").addClass("active");
				}
			});

			owl.each(function(){
				$(".owl-item").find(".owlTxt").clone().appendTo(slideTextList);
			});

			slideTextList.find(".owlTxt:first-child").addClass("active");

			$(".owlNext").click(function() {
			    owl.trigger('owl.next');
			});

			$(".owlPrev").click(function() {
			    owl.trigger('owl.prev');
            });

        });
        /*  */

