jQuery(document).ready(

    function() {
       // Handle staff listing search
        jQuery('#cat').change(function() {
            UpdateSearchOptions(this);
        });


        jQuery('#cat').keyup(function() {
            UpdateSearchOptions(this);
        });

    }
);

    function UpdateSearchOptions(obj) {
	if (obj.options[obj.selectedIndex].id == 'staff-selector') {
        var input = jQuery('#post_type');
        input.val('staff_listing');
        var input = jQuery('#orderby');
        input.val('post_title');
}
if (obj.options[obj.selectedIndex].id == 'news-selector') {
        var input = jQuery('#post_type');
        input.val('post');
        var input = jQuery('#orderby');
        input.val('post_title');
}
    }

