jQuery(this).parent('.got_annoucement').hide();
 if($show_news!=null){
    var date = new Date();
date.setTime(date.getTime() + (1*24*60*60*1000));
   jQuery.cookie('my_cookie_name',$show_news,{ expires: date }); 
}else{
   jQuery.cookie('my_cookie_name','last',{ expires: date }); 
}
});
