link.script:
$(document).ready(function() {
	
	//make sure the elements are used
	try{
		if ($('#responsive').length){
			$('.leftnavShell').hide();
			$('.main_body_size').css('width','100%');
			$('.middle_when_no_right_side_bar').css('padding-left','0px');
			$('.middle_when_right_side_bar').css('padding-left','0px');
		}else{
			if ($('#menu').length || $('#sidebar').length){
			
				if ($('#menu').length){
					var navElement = 'menu';
				}else{
					var navElement = 'sidebar';
				}
				var navWidth = $('#' + navElement).outerWidth(true);
				var mainWidth = $('.main_body_size').outerWidth();
				var middleWithRightBar = $('.middle_when_right_side_bar').width();
				var middleWithNoRightBar = $('.middle_when_no_right_side_bar').width();
				
				$('#' + navElement).hide();
				
				$('#sidebar .below_left_nav').hide();
				$('.main_body_size').css('width',mainWidth + navWidth + 'px');
				$('.middle_when_right_side_bar').css('width',middleWithRightBar + navWidth + 'px');
				$('.middle_when_no_right_side_bar').css('width',middleWithNoRightBar + navWidth + 'px');
				
			}
		}
	}
	catch(e){
		//do nothing
	}
});

