function googleTranslateElementInit() {
  new google.translate.TranslateElement({
		pageLanguage: 'en',
		includedLanguages: 'en,ar,de,es,fr,hr,it,ja,ko,nl,pl,pt,ro,ru,sr,tl,vi,zh-CN,zh-TW',
	}, 'google_translate_element');

	// Replace the select element
	jcf.replaceAll();

	// Place the text in the jcf select element since
	// it's not happening automatically
	jQuery("#google_translate_element .jcf-select-text span").html("Select Language");
}

// Initialize slick homepage sliders
function initSlickCarousel() {
	jQuery('#cycle-gallery:not(.js-responsive-gallery)').slick({
		slidesToScroll: 1,
		fade: true,
		adaptiveHeight: true
	});

	jQuery('#factoids-slider').each(function() {
		var gallery = jQuery(this);
		var initTimer;

		function slickTablet() {
			gallery.slick({
				slidesToScroll: 1,
				slidesToShow: 3,
				adaptiveHeight: true,
				autoplaySpeed: 5000,
				responsive: [
					{
						breakpoint: 991,
						settings: {
							slidesToShow: 2
						}
					},
						{
						breakpoint: 768,
						settings: {
							slidesToShow: 1
						}
					}
				]
			});
		}

		function slickMobile() {
			gallery.slick({
				slidesToShow: 1,
				slidesToScroll: 1,
				rows: 2,
				autoplaySpeed: 5000,
				adaptiveHeight: true,
				slidesPerRow: 1
			});
		}

		ResponsiveHelper.addRange({
			'768..': {
				on: function() {
					clearTimeout(initTimer);
					initTimer = setTimeout(function() {
						if (gallery.hasClass('slick-initialized')) {
							gallery.slick('unslick');
							slickTablet();
						} else {
							slickTablet();
						}
					}, 500);
				}
			},

			'..767': {
				on: function() {
					clearTimeout(initTimer);
					initTimer = setTimeout(function() {
						if (gallery.hasClass('slick-initialized')) {
							gallery.slick('unslick');
							slickMobile();
						} else {
							slickMobile();
						}
					}, 500);
				}
			}
		});
	});
}

// Typed out text in hero
function initTypedText() {
	var defaultOptions = {
		wordDelay:		400,
		letterDelay:	100,
		initedClass:	'js-typed-text-inited'
	};

	jQuery('.js-typed-text').each(function() {
		var holder        = jQuery(this);
		var options       = jQuery.extend({}, defaultOptions, holder.data('typedOptions'));
		var words         = holder;
		var letterCounter = 0;

		words.each(function(ind) {
			var word      = jQuery(this);
			var text      = word.text().split('');
			var wordDelay = ind * options.wordDelay;

			word.html('');

			jQuery.each(text, function() {
				var letterItem = jQuery('' + this + '').css({ transitionDelay: wordDelay + letterCounter * options.letterDelay + 'ms'});
				letterItem.appendTo(word);
				letterCounter++;
			});
		});

		holder.addClass(options.initedClass);
		holder.on('startAnimation', function() {
			holder.addClass('js-anim-active');
		});
	});
}

//== HorizontalAccordion for calendar
function initHorizontalAccordion() {
	jQuery('.js-horizontal-accordion').horizontalAccordion({
		item: '.js-accordion-item',
		opener: '.js-accordion-opener',
		slide: '.js-accordion-slide',
		slideContent: '.js-accordion-slide-content',
		activeClass: 'js-accordion-active',
		animSpeed: 200,
		collapsible: false
	});
}

jQuery(document).ready(function() {
	initTypedText();
	initHorizontalAccordion();
	initSlickCarousel();

	jQuery(window).on('load masonry/refresh', function() {
		jQuery('#instafeed').masonry('layout');
	});
});

